import React, { useContext, useEffect, useRef , useState} from "react";
import noteContext from "../context/notes/noteContext";
import AddNote from "./AddNote";
import NoteItem from "./NoteItem";

const Notes = (props) => {
  const context = useContext(noteContext);
  const { notes, getNotes, editNote } = context;
  useEffect(() => {
    getNotes();
    // eslint-disable-next-line
  }, []);
  const ref = useRef(null);
  const refClose = useRef(null)
  const[note, setNote]=useState({eid:"", etitle:"", edescription:"", etag:"default"});

  const updateNote = (currentNote) => {
    ref.current.click();
    setNote({eid: currentNote._id, etitle: currentNote.title, edescription: currentNote.description, etag: currentNote.tag});
    
  };

const handleClick=(e)=>{  
  editNote(note.eid, note.etitle, note.edescription, note.etag);
  refClose.current.click();
  props.showAlert("Updated Successfully", "success");
}

const onChange=(e)=>{
  setNote({...note, [e.target.name]:e.target.value})
}
  return (
    <>
      <AddNote showAlert={props.showAlert}/>
      <button ref={ref} type="button" className="btn btn-primary d-none" data-bs-toggle="modal" data-bs-target="#exampleModal">
        Launch demo modal
      </button>
      <div className="modal fade"  id="exampleModal"  tabIndex="-1"   aria-labelledby="exampleModalLabel"
        aria-hidden="true">
        <div className="modal-dialog">
          <div className="modal-content">
            <div className="modal-header">
              <h5 className="modal-title" id="exampleModalLabel">  Edit Note  </h5>
              <button  type="button" className="btn-close"  data-bs-dismiss="modal"  aria-label="Close" ></button>
            </div>
            <div className="modal-body">
              <form className="my-4">
                <div className="mb-3">
                  <label htmlFor="etitle" className="form-label">Title</label>
                  <input type="text" className="form-control"  id="etitle" name='etitle' value={note.etitle}
                  aria-describedby="emailHelp" onChange={onChange} minLength={2} required/>            
                </div>

                <div className="mb-3">
                  <label htmlFor="edescription" className="form-label">Description</label>
                  <textarea  rows={4} cols={40}  className="form-control" id="edescription"  name="edescription"
                    value={note.edescription}  onChange={onChange} minLength={1} required/>
                </div>
                <div className="mb-3">
                  <label htmlFor="etag" className="form-label">Tag</label>
                  <input type="text" className="form-control" id="etag"  name="etag"
                    value={note.etag}  onChange={onChange}/>
                </div>
              </form>
            </div>
            <div className="modal-footer">
              <button ref={refClose} type="button"  className="btn btn-secondary"  data-bs-dismiss="modal"  >
                Close
              </button>
              <button disabled={note.etitle.length<2 || note.edescription.length<1} 
                  type="button" onClick={handleClick} className="btn btn-primary">  Update Note  </button>
            </div>
          </div>
        </div>
      </div>

      <div className="container row my-3">
        <h2>Your Notes</h2>
        <div className="container">
        {notes.length===0 && ' No notes to display'}
        </div>
        {notes.map((note) => {
          return (
            <NoteItem key={note._id} updateNote={updateNote} note={note} showAlert={props.showAlert}/>
          );
        })}
      </div>
    </>
  );
};

export default Notes;
