import React, { useContext, useEffect, useRef } from "react";
import noteContext from "../context/notes/noteContext";
import AddNote from "./AddNote";
import NoteItem from "./NoteItem";

const Notes = () => {
  const context = useContext(noteContext);
  const { notes, getNotes } = context;
  useEffect(() => {
    getNotes();
    // eslint-disable-next-line
  }, []);

  const updateNote = (note) => {
    ref.current.click();
  };
const ref = useRef(null);
  return (
    <>
      <AddNote />
      <button ref={ref} type="button" class="btn btn-primary" data-bs-toggle="modal" data-bs-target="#exampleModal">
        Launch demo modal
      </button>
      <div className="modal fade"  id="exampleModal"  tabindex="-1"   aria-labelledby="exampleModalLabel"
        aria-hidden="true">
        <div className="modal-dialog">
          <div className="modal-content">
            <div className="modal-header">
              <h5 className="modal-title" id="exampleModalLabel">  Edit Note  </h5>
              <button  type="button" className="btn-close"  data-bs-dismiss="modal"  aria-label="Close" ></button>
            </div>
            <div className="modal-body">
              <form className="my-4">
                <div className="mb-3">
                  <label htmlFor="title" className="form-label">Title</label>
                  <input type="text" className="form-control"  id="title" name='title'
                  aria-describedby="emailHelp" onChange={onChange}/>            
                </div>

                <div className="mb-3">
                  <label htmlFor="description" className="form-label">Description</label>
                  <input type="text" className="form-control" id="description"  name="description"
                      onChange={onChange}/>
                </div>
                <div className="mb-3">
                  <label htmlFor="tag" className="form-label">Tag</label>
                  <input type="text" className="form-control" id="tag"  name="tag"
                      onChange={onChange}/>
                </div>
                
                <button type="submit" onClick={handleClick} className="btn btn-primary">
                  Add Note
                </button>
              </form>
            </div>
            <div className="modal-footer">
              <button  type="button"  className="btn btn-secondary"  data-bs-dismiss="modal"  >
                Close
              </button>
              <button type="button" className="btn btn-primary">  Update Note  </button>
            </div>
          </div>
        </div>
      </div>

      <div className="row my-3">
        <h2>Your Notes</h2>
        {notes.map((note) => {
          return (
            <NoteItem key={note._id} updateNote={updateNote} note={note} />
          );
        })}
      </div>
    </>
  );
};

export default Notes;
