import { useState } from "react";
import NoteContext from "./noteContext";

const NoteState = (props) => {
  const host = "http://localhost:5000";
  const notesInitial = [];
  const [notes, setNotes] = useState(notesInitial);

  //Get all notes
  const getNotes = async () => {
    //TODO: Api call
    const response = await fetch(`${host}/api/notes/fetchallnotes`, {
      method: "GET",
      headers: {
        "Content-Type": "application/json",
        "auth-token":
          "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyIjp7ImlkIjoiNjNhMWMzNDAxMzRlYjNkODZhYmVhNTljIn0sImlhdCI6MTY3MTk1MDk0Mn0.j-87fMxifNh3ueO7E6hoCC7G3QHAraSvw8PWhqoCcgg",
      },
    });
    const json = await response.json();
    console.log(json);
    setNotes(json);
  };

  //Add a note
  const addNote = async (title, description, tag) => {
    //TODO: Api call
    const response = await fetch(`${host}/api/notes/addnote`, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        "auth-token":
          "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyIjp7ImlkIjoiNjNhMWMzNDAxMzRlYjNkODZhYmVhNTljIn0sImlhdCI6MTY3MTk1MDk0Mn0.j-87fMxifNh3ueO7E6hoCC7G3QHAraSvw8PWhqoCcgg",
      },
      body: JSON.stringify({ title, description, tag }),
    });
    const json = response.json();
    console.log(json);

    console.log("adding a new note");
    const note = {
      _id: "63a5e0424bbd93564909d0c496f",
      user: "63a30cfd6b004fee61e9ad3a",
      title: title,
      description: description,
      tag: tag,
      date: "2022-12-23T17:06:44.578Z",
      __v: 0,
    };
    setNotes(notes.concat(note));

  };

  //Delete a Note
  const deleteNote = async (id) => {
    //TODO: Api call
    const response = await fetch(`${host}/api/notes/deletenote/${id}`, {
      method: "DELETE",
      headers: {
        "Content-Type": "application/json",
        "auth-token":
          "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyIjp7ImlkIjoiNjNhMWMzNDAxMzRlYjNkODZhYmVhNTljIn0sImlhdCI6MTY3MTk1MDk0Mn0.j-87fMxifNh3ueO7E6hoCC7G3QHAraSvw8PWhqoCcgg",
      },
    });
    const json = response.json();
    console.log(json);

    console.log("deleted a note with id" + id);
    const newNotes = notes.filter((note) => {
      return note._id !== id;
    });
    setNotes(newNotes);
  };

  //Edit a Note
  const editNote = async (id, title, description, tag) => {
    //api call
    const response = await fetch(`${host}/api/notes/updatenote/${id}`, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        "auth-token":
          "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyIjp7ImlkIjoiNjNhMWMzNDAxMzRlYjNkODZhYmVhNTljIn0sImlhdCI6MTY3MTk1MDk0Mn0.j-87fMxifNh3ueO7E6hoCC7G3QHAraSvw8PWhqoCcgg",
      },
      body: JSON.stringify({ title, description, tag }),
    });
    const json = response.json();
    console.log(json);

    //logic to edit in client
    for (let index = 0; index < notes.length; index++) {
      const element = notes[index];
      if (element._id === id) {
        element.title = title;
        element.description = description;
        element.tag = tag;
      }
    }
  };

  return (
    <NoteContext.Provider
      value={{ notes, addNote, deleteNote, editNote, getNotes }}
    >
      {props.children}
    </NoteContext.Provider>
  );
};

export default NoteState;
