import React, { useContext, useEffect } from 'react'
import noteContext from '../context/notes/noteContext'

const Home = () => {
  const a = useContext(noteContext);
  useEffect(()=>{
    a.update()
  }, [])   
  return (
    <div>
      this is about {a.state.name} and i m in class {a.state.class}
    </div>
  )
}

export default Home
