import React, { useState, useContext } from 'react'
import noteContext from "../context/notes/noteContext"

const AddNote = () => {
    const context = useContext(noteContext);
    const { addNote } = context;    
    const[note, setNote]=useState({title:"", description:"", tag:"default"});

    const handleClick=(e)=>{
        e.preventDefault();
        addNote(note.title, note.description, note.tag);  
        setNote({title:"", description:"", tag:""})
    }
    const onChange=(e)=>{
        setNote({...note, [e.target.name]:e.target.value})
    }

  return (
    <div className="container my-3 ">
        <h2>Add a Note</h2>
        <form className="my-4">
          <div className="mb-3">
            <label htmlFor="title" className="form-label">Title</label>
            <input type="text" className="form-control"  id="title" name='title'
            aria-describedby="emailHelp" onChange={onChange} minLength={2} required/>            
          </div>

          <div className="mb-3">
            <label htmlFor="description" className="form-label">Description</label>
            <textarea  rows={4} cols={40} className="form-control" id="description"  name="description"
                onChange={onChange} minLength={1} required/>
          </div>
          <div className="mb-3">
            <label htmlFor="tag" className="form-label">Tag</label>
            <input type="text" className="form-control" id="tag"  name="tag"
                onChange={onChange}/>
          </div>
          <button disabled={note.title.length<2 || note.description.length<1} type="submit" onClick={handleClick} className="btn btn-primary">
            Add Note
          </button>
        </form>
      </div>
  )
}

export default AddNote
